<template>
  <v-container class="fill-height">
    <v-responsive class="align-center text-center fill-height">
      <v-row class="d-flex align-center justify-center">
        <v-col cols="auto">
          <submit-task :tasks="tasks" />
        </v-col>
      </v-row>
      <v-col>
        <v-app-bar-title class="pa-3">Active tasks</v-app-bar-title>
        <Tasks
          :tasks="activeTasks"
          @taskEvent="toggleTask"
          :fullTaskList="tasks"
        />
        <v-app-bar-title class="pa-3"> Completed tasks </v-app-bar-title>
        <Tasks
          :tasks="finishedTasks"
          @taskEvent="toggleTask"
          :fullTaskList="tasks"
        />
      </v-col>
    </v-responsive>
  </v-container>
</template>

<script lang="ts">
import SubmitTask from "./SubmitTask.vue";
import Tasks from "./Tasks.vue";
interface Task {
  src: string;
  title: string;
  text: string;
  active: boolean;
}
export default {
  data: () => ({
    tasks: [
      {
        title: "Feed my owl",
        src: "data:image/jpeg;base64,",
        text: "Need to feed my cute owl",
        active: true,
      },
      {
        title: "Feed my cat",
        src: "https://st3.depositphotos.com/20524830/32366/i/450/depositphotos_323668254-stock-photo-portrait-beautiful-cat-pallas-cat.jpg",
        text: "Need to feed my cute cat",
        active: false,
      },
    ],
  }),
  methods: {
    toggleTask(task: Task) {
      task.active = !task.active;
    },
    updateTaskList(updatedTaskList: Task[]) {
      this.tasks = updatedTaskList;
    },
  },
  computed: {
    activeTasks(): Task[] {
      return this.tasks.filter((task) => task.active);
    },
    finishedTasks(): Task[] {
      return this.tasks.filter((task) => !task.active);
    },
  },
  components: { SubmitTask, Tasks },
};
</script>
